DROP database IF EXISTS pageone;
create database pageone;
use pageone;

DROP TABLE IF EXISTS users;
CREATE TABLE `users` (
  `user_id` int NOT NULL AUTO_INCREMENT,
  `first_name` varchar(45) DEFAULT NULL,
  `last_name` varchar(45) DEFAULT NULL,
  `email` varchar(45) DEFAULT NULL,
  `password` varchar(200) DEFAULT NULL,
  `admin` tinyint DEFAULT '0',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `email_UNIQUE` (`email`)
);


DROP TABLE IF EXISTS panier;
CREATE TABLE `panier` (
  `panier_id` int NOT NULL AUTO_INCREMENT,
  `user_id` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`panier_id`)
);

DROP TABLE IF EXISTS panier_item;
CREATE TABLE `panier_item` (
  `id_panier_item` int NOT NULL AUTO_INCREMENT,
  `id_panier` varchar(45) DEFAULT NULL,
  `book_id` varchar(45) DEFAULT NULL,
  `supply` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`id_panier_item`)
);

DROP TABLE IF EXISTS books;
CREATE TABLE books(
  `book_id` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,	
   image                 VARCHAR(200) NOT NULL default 'https://www.hachette.co.nz/graphics/CoverNotAvailable.jpg'
  ,name                  VARCHAR(200) NOT NULL
  ,author                VARCHAR(50) default ''
  ,format                VARCHAR(20) default ''
  ,book_depository_stars NUMERIC(3,1) default 0
  ,price                 NUMERIC(5,2) default 0
  ,supply	 			 NUMERIC(65) default 100
  ,currency              VARCHAR(1) default '$'
  ,old_price             NUMERIC(5,2) default 0
  ,isbn                  VARCHAR(88) default 0 
  ,category              VARCHAR(7) default ''
  ,img_paths             VARCHAR(27) default ''
);
UPDATE books SET name = 'you', price = 10, book_depository_stars = 5, supply = 200 WHERE book_id = 1;


